import Layout from '../components/Layout';

const About = () => (
    <Layout>
        <div>
            <h1>About BitPrice</h1>
            <p>Your one stop destination to view Bitcoin prices</p>
            <p>This is a Demo React Next Application which uses React and NextJS libraries to render the pages at server side</p>
            <p>Usually React Apps are client side applications the source code executes at client machine 
                but there are some scenarios where
                rendering pages at server gives us some power to boost our app speed and enhance the UX
            </p>
        </div>
    </Layout>
);

export default About;
